import React from "react";
import Select from 'react-select';
import { useEffect, useState } from "react";
import {
  helloWorldContract,
  connectWallet,
  updateMessage,
  loadCurrentMessage,
  getCurrentWalletConnected,
  vote,
  addPerson,
  loadCurrentPeople,
  votingContract
} from "./util/interact.js";

import alchemylogo from "./alchemylogo.svg";

const HelloWorld = () => {
  //state variables
  const [options, setOptions] = useState([]);
  const [showVoteDropdown, setShowVoteDropdown] = useState(false); 
  const [voteChoice, setVoteChoice] = useState(""); 
  const [walletAddress, setWallet] = useState("");
  const [status, setStatus] = useState("");
  const [message, setMessage] = useState("No connection to the network. lol"); //default message
  const [newMessage, setNewMessage] = useState("");

  //called only once
  useEffect(async () => {
    const message = await loadCurrentMessage();
    setMessage(message);
    addSmartContractListener();

    const {address, status} = await getCurrentWalletConnected();
    setWallet(address);
    setStatus(status);

    addWalletListener();

  }, []);

  function addSmartContractListener() {
    helloWorldContract.events.UpdatedMessages({}, (error, data) => {
      if (error) {
        setStatus("😥 " + error.message);
      } else {
        setMessage(data.returnValues[1]);
        setNewMessage("");
        setStatus("🎉 Your message has been updated!");
      }
    });

    votingContract.events.PersonAdded({}, (error, data) => {
      if (error) {
        setStatus("😥 " + error.message);
      } else {
        console.log("Data: " + data);
        console.log("Data.returnValues[0]: " + data.returnValues[0]);

        const refinedOptions = data.returnValues[0].map((name) => { 
          return {value: name, label: name}}
        );

        console.log("Refined Options: " + refinedOptions);

        setOptions(refinedOptions);
      }
    });
  }

  function addWalletListener() {
  if (window.ethereum) {
    window.ethereum.on("accountsChanged", (accounts) => {
      if (accounts.length > 0) {
        setWallet(accounts[0]);
        setStatus("👆🏽 Write a message in the text-field above.");
      } else {
        setWallet("");
        setStatus("🦊 Connect to Metamask using the top right button.");
      }
    });
  } else {
    setStatus(
      <p>
        {" "}
        🦊{" "}
        <a target="_blank" href={`https://metamask.io/download.html`}>
          You must install Metamask, a virtual Ethereum wallet, in your
          browser.
        </a>
      </p>
    );
  }
}

  const connectWalletPressed = async () => {
    const walletResponse = await connectWallet();
    setStatus(walletResponse.status);
    setWallet(walletResponse.address);
  };

  const onUpdatePressed = async () => {
    //remove this and set show vote dropdown when 
    setShowVoteDropdown(true);
    const { status } = await addPerson(walletAddress, newMessage);
    setStatus(status);
  };

  const onVotePressed = async () => {
    // await vote(voteChoice);
  };


  options = [
    { value: 'chocolate', label: 'Chocolate' },
    { value: 'strawberry', label: 'Strawberry' },
    { value: 'vanilla', label: 'Vanilla' }
  ]

  //the UI of our component
  return (
    <div id="container">
      <img id="logo" src={alchemylogo}></img>
      <button id="walletButton" onClick={connectWalletPressed}>
        {walletAddress.length > 0 ? (
          "Connected: " +
          String(walletAddress).substring(0, 6) +
          "..." +
          String(walletAddress).substring(38)
        ) : (
          <span>Connect Wallet</span>
        )}
      </button>

      <h2 style={{ paddingTop: "1px" }}>Please enter your name here:</h2>

      <div>
        <input
          type="text"
          placeholder="First Last"
          onChange={(e) => setNewMessage(e.target.value)}
          value={newMessage}
        />
        {/* <p id="status">{status}</p> */}

        <button id="register" onClick={onUpdatePressed}>
          Register
        </button>
      </div>
      { showVoteDropdown && (
          <div style={{ paddingTop: "18px" }}>
          <Select options={options} onChange={(e) => setVoteChoice(e.value)}/>

          <button id="vote" onClick={onVotePressed}>
            Vote
          </button>
          </div>
        )}
    </div>
  );
};

export default HelloWorld;
